using PdfSharpCore.Drawing;
using PdfSharpCore.Pdf;
using RecetaElectronica.Modelo;
using System.IO;

namespace RecetaElectronica.Servicios
{
    public class PdfSharpRecetaService
    {
        public byte[] GenerateRecetaPdf(Recetas receta)
        {
            using (var document = new PdfDocument())
            {
                var page = document.AddPage();
                XGraphics gfx = XGraphics.FromPdfPage(page);
                XFont titleFont = new XFont("Arial", 20, XFontStyle.Bold);
                XFont regularFont = new XFont("Arial", 12, XFontStyle.Regular);

                int yPoint = 40;

                // Título
                gfx.DrawString("Receta Médica", titleFont, XBrushes.DarkBlue, new XRect(0, yPoint, page.Width, 0), XStringFormats.TopCenter);
                yPoint += 40;

                // Datos del paciente y médico
                gfx.DrawString($"Paciente: {receta.Paciente}", regularFont, XBrushes.Black, 40, yPoint); yPoint += 20;
                gfx.DrawString($"Médico: {receta.Medico.Nombre} {receta.Medico.Apellido} - Matricula: {receta.Medico.Matricula}", regularFont, XBrushes.Black, 40, yPoint); yPoint += 20;
                gfx.DrawString($"Obra Social: {receta.Cobertura.ObraSocial.Nombre}", regularFont, XBrushes.Black, 40, yPoint); yPoint += 20;
                gfx.DrawString($"Cobertura: {receta.Cobertura.Nombre}", regularFont, XBrushes.Black, 40, yPoint); yPoint += 30;

                // Medicamentos
                gfx.DrawString("Medicamentos:", regularFont, XBrushes.Black, 40, yPoint); yPoint += 20;
                foreach (var med in receta.RecetaMedicamentos)
                {
                    gfx.DrawString($"- {med.Medicamento.Nombre}", regularFont, XBrushes.Black, 60, yPoint);
                    yPoint += 20;
                }

                // Pie de página con logo y nombres
                string base64Image = "data:image/jpeg;base64,"; // Pegá el base64 completo acá
                var imageBytes = Convert.FromBase64String(base64Image.Split(',')[1]);
                XImage logo = XImage.FromStream(() => new MemoryStream(imageBytes));
                gfx.DrawImage(logo, 50, page.Height - 300, page.Width - 100, 250);

                yPoint = (int)page.Height - 30;
                gfx.DrawString("Alderete Exquiel | Juan Martin Viana | Lulusis Mauricio | Veron Octavio | Agusti Lautaro | Navarro Valentino",
                               new XFont("Arial", 8, XFontStyle.Italic),
                               XBrushes.Gray,
                               new XRect(0, yPoint, page.Width, 0),
                               XStringFormats.TopCenter);

                // Exportar a byte[]
                using (var stream = new MemoryStream())
                {
                    document.Save(stream);
                    return stream.ToArray();
                }
            }
        }
    }
}

